query.sql:
/* SCH-04_templates.named_queries.xml
create the TEST templates for each department using the SCHEDULE tables
This query is for testing against the up-comming school year */ select distinct 'course template' as "type", 'UPDATE' as "action", 'Templ_TEST_'||CC.SCHOOLID||'_'||COURSES.SCHED_DEPARTMENT as "code", COURSES.SCHED_DEPARTMENT as "name", '' as "start_date", '' as "end_date", '1' as "is_active", CC.SCHOOLID||'_TEST_'||COURSES.SCHED_DEPARTMENT as "department_code", '' as "template_code", '' as "semester_code", '' as "offering_code", '' as "custom_code" from COURSES COURSES, STUDENTS STUDENTS, SCHEDULECC CC, SCHOOLS SCHOOLS where CC.STUDENTID=STUDENTS.ID and CC.SCHOOLID=SCHOOLS.SCHOOL_NUMBER and CC.COURSE_NUMBER=COURSES.COURSE_NUMBER and CC.TERMID >= case when (EXTRACT(month from sysdate) >= 1 and EXTRACT(month from sysdate) <= 6) THEN (EXTRACT(year from sysdate)-2000+10)*100 when (EXTRACT(month from sysdate) >= 7 and EXTRACT(month from sysdate) <= 12) THEN (EXTRACT(year from sysdate)-2000+11)*100 end and length(COURSES.SCHED_DEPARTMENT) > 0 order by "code" asc
