query.sql:
/* 08_e_p_drop.named_queries.xml Update enrollments for parents: drop parents from classes that students have dropped */ select 'enrollment' as "type", 'DELETE' as "action", 'P_'||guardian.guardianid as "child_code", 'Learner' as "role_name", 'cs_'||cc.schoolid||'_'||cc.course_number||'_'||REGEXP_REPLACE(cc.termid, '\D+', '')||'_'||DECODE(substr(cc.expression, 1, 1),
1, 'A', 2, 'B', 3, 'C', 4, 'D', 5, 'E', 6, 'F', 7, 'G', 8, 'H', 9, 'ADV', 'UNKNOWN') as "parent_code" from cc join students on cc.studentid = students.id join sections on CC.OrigSectionID = Sections.ID join courses on sections.course_number = courses.course_number join teachers on sections.teacher = teachers.id join guardianstudent on students.dcid = guardianstudent.studentsdcid join guardian on guardianstudent.guardianid = guardian.guardianid
where sections.TERMID >= case when (EXTRACT(month from sysdate) >= 1 and EXTRACT(month from sysdate) <= 6) THEN (EXTRACT(year from sysdate)-2000+9)*100 when (EXTRACT(month from sysdate) >= 7 and EXTRACT(month from sysdate) <= 12) THEN (EXTRACT(year from sysdate)-2000+10)*100 end -- only select students that are "active" and students.enroll_status IN (0) -- only select students grades 5+ and students.grade_level >= 5 order by "child_code" desc
