query.sql:
/* Update enrollments for parents: drop parents from classes that students have dropped */ -- 08_e_p_drop enrol parents; drop courses
SELECT DISTINCT -- cc.termid, -- sections.termid AS "sections.termid", 'enrollment' AS "type", 'UPDATE' AS "action", 'P_'||guardian.guardianid AS "child_code", 'Parent' AS "role_name",
/* * use the following format to ensure that section codes are unique * and remain constant even if the teacher is reassigned * * cs_cc.schoolid_cc.course_number_cc.termid_cc.expression_cc.sectionid * code: cs_3_SPHYIBS2_3200_D_28636 * * NOTE: X block is added AS a place-holder for courses that do not appear on the * schedule such AS MS Enrichment, self study IB courses SSST */ 'cs_' || cc.schoolid || '_' || cc.course_number || '_' || REGEXP_REPLACE(cc.termid, '[^0-9]+', '') || '_' || DECODE(regexp_replace(cc.expression, '[^0-9]', ''), 1, 'A', 2, 'B', 3, 'C', 4, 'D', 5, 'E', 6, 'F', 7, 'G', 8, 'H', 9, 'ADV', 10, 'X', 'UNKNOWN' ) ||'_' || REGEXP_REPLACE(cc.sectionid, '[^0-9]+', '') AS "parent_code"
FROM cc JOIN students s ON cc.studentid = s.id JOIN sections ON cc.origsectionid = sections.id JOIN courses ON sections.course_number = courses.course_number JOIN guardianstudent gs ON s.dcid = gs.studentsdcid JOIN guardian ON gs.guardianid = guardian.guardianid
WHERE sections.termid >= (SELECT TRUNC( MAX(termid) /10 ) * 10 FROM cc) AND s.enroll_status IN (0) AND s.grade_level >= 5
ORDER BY "child_code" DESC
