query.sql:
/* * c08_e_t_school.named_queries.xml * Add all staff to their home schools * Default role: Instructor * Use PS SIS assigned roles to elevate to Admin role */ SELECT DISTINCT 'enrollment' AS "type", 'UPDATE' AS "action", 'T_' || users.teachernumber AS "child_code",
/* * Use the role assigned in PowerSchool SIS * this ensures that the PS SIS assigned role is valid at the organization levels * in Brightspace. */ CASE WHEN LOWER(uf.brightspace_account_type) LIKE '%-org%' THEN uf.brightspace_account_type ELSE 'Instructor' END AS "role_name", users.homeschoolid AS "parent_code" FROM users JOIN schoolstaff ON schoolstaff.users_dcid = users.dcid LEFT JOIN u_schoolstaffuserfields uf ON schoolstaff.dcid = uf.schoolstaffdcid WHERE /* * Only the homeschool information for each user; * this prevents multiple entries */ users.homeschoolid = schoolstaff.schoolid
/* * Ignore all users with no email address */ AND LENGTH(users.EMAIL_ADDR) > 0
/* * Only active staff */ AND schoolstaff.status = 1 ORDER BY "child_code" ASC
